bind.calculate:
if(
 selected( /child_assessment/data/__any_danger , 'no') and 
  /child_assessment/cough/cough_time  < 14 and
 (
 selected( /child_assessment/data/__fast_breathing_child , 'yes') or 
 selected( /child_assessment/data/__fast_breathing_person , 'yes')
 )
 ,"yes", "no"
 )

if(
 selected( /child_assessment/data/__any_danger , 'no') and
 (
 selected( /child_assessment/cough/cough_yes_no , 'yes') or
 selected( /child_assessment/diarrhea/diarrhea_yes_no , 'yes') or
 selected( /child_assessment/malaria/malaria_results , 'positive')
 )
 ,"yes", "no"
 )

if(../../patient_age_in_days < 31, 
concat(../../patient_age_in_days, ' days old'),
if(../../patient_age_in_months < 12, 
concat(../../patient_age_in_months, ' months old'), 
concat(../../patient_age_in_years, ' years old')))
