bind.calculate:
if(
 selected( /child_assessment/any_danger , 'no') and 
  /child_assessment/g_cough/cough_time  < 14 and
 (
 selected( /child_assessment/fast_breathing_child , 'yes') or 
 selected( /child_assessment/fast_breathing_person , 'yes')
 )
 ,"yes", "no"
 )

if(
 selected( /child_assessment/any_danger , 'no') and
 (
 selected( /child_assessment/g_cough/cough_yes_no , 'yes') or
 selected( /child_assessment/g_diarrhea/diarrhea_yes_no , 'yes') or
 selected( /child_assessment/g_malaria/malaria_results , 'positive')
 )
 ,"yes", "no"
 )

if( /child_assessment/patient_age_in_days  < 31, 
concat( /child_assessment/patient_age_in_days , ' days old'),
if( /child_assessment/patient_age_in_months  < 12, 
concat( /child_assessment/patient_age_in_months , ' months old'), 
concat( /child_assessment/patient_age_in_years , ' years old')))
